header = '''
   ______________________________
 / \  Dear %s,%s\.
|   |                            |.'''
max_header_length = 21

first_line = " \_ | %s|."
line_max_length = 27

body = "    | %s|."
footer = '''    |                            |.
    | Yours,                     |.
    | Tomer Eliel                |.
    | MAGSHIMIM-NETWORK-2023     |.
    |   _________________________|___
    |  /                            /.
    \_/____________________________/.
'''


def create_ascii_frame(text, student_name):
    frame = [header % (student_name, " " * (max_header_length - len(student_name)))]
    lines = text.split('\n')
    if len(lines[0]) > line_max_length:
        first_line_words = lines[0].split(" ")
        new_first_line = ""
        while len(new_first_line) < line_max_length and len(first_line_words) > 0:
            new_first_line += first_line_words[0]
            new_first_line += " "
            first_line_words = first_line_words[1:]
        up_lines = [" ".join(first_line_words)]
        up_lines.extend(lines[1:])
        lines = up_lines
        frame.append(first_line % (new_first_line[:-1]))
    else:
        frame.append(first_line % (lines[0] + " " * (line_max_length - len(lines[0]))))

    for line in lines:
        while len(line) > line_max_length:
            sub_line = line[:line_max_length]
            frame.append(body % sub_line)
            line = line[line_max_length:]
        frame.append(body % (line + " " * (line_max_length - len(line))))

    frame.append(footer)
    return '\n'.join(frame)




