def bless(studentName):
    clear()
    id = input("What is your id (Tehudat-Zehut)?\n")
    while hashlib.md5(id.encode()).digest() != IDS[studentName]:
      clear()
      id = input("What is your id (Tehudat-Zehut)?\n")
    clear()
    sleep(3)
    foramlSudentName = studentName[0].upper()+ studentName[1:]
    print(create_ascii_frame(BLESSINGS[studentName],foramlSudentName))
    f = open("To "+str(foramlSudentName) +".txt",'w')
    f.write(HEB_BLESS[studentName])
    f.close()
    print("'To "+foramlSudentName+".txt' file was just created, READ IT!")
    sleep(5)
    keyboard.press('f11')
    sleep(40)
    os.system('exit')



